static void
impl_attach_window (EphyExtension *extension, EphyWindow *window)
{
	GtkActionGroup *action_group;
	GtkUIManager *manager;
	guint merge_id;

	static GtkActionEntry action_entries [] =
	{
	{ "MyAction", NULL, N_("Menu Entry Name"), NULL, /* shortcut key */ N_("Does Whatever I Want"), G_CALLBACK (my_action_activate_cb) }
	};
	static const guint n_action_entries = G_N_ELEMENTS (action_entries);

	manager = GTK_UI_MANAGER (window->ui_merge);

	action_group = gtk_action_group_new ("EphyFooExtensionActions");

	gtk_action_group_set_translation_domain (action_group, GETTEXT_PACKAGE);
	gtk_action_group_add_actions (action_group, action_entries, n_action_entries, window);

	gtk_ui_manager_insert_action_group (manager, action_group, 0);
	g_object_unref (action_group);

	merge_id = gtk_ui_manager_new_merge_id (manager);

	gtk_ui_manager_add_ui (manager, merge_id, "/menubar/GoMenu", "MyActionSep", NULL, GTK_UI_MANAGER_SEPARATOR, TRUE);
	gtk_ui_manager_add_ui (manager, merge_id, "/menubar/GoMenu", "MyAction", "MyAction", GTK_UI_MANAGER_MENUITEM, TRUE);
}

static void
impl_attach_window (EphyExtension *extension, EphyWindow *window)
{
	GtkStatusbar *statusbar;
	guint context_id;

	statusbar = GTK_STATUSBAR (ephy_window_get_statusbar (window));
	g_return_if_fail (GTK_IS_STATUSBAR (statusbar));

	context_id = gtk_statusbar_get_context_id (statusbar, "Foo extension");

	g_object_set_data (G_OBJECT (window), "foo-statusbar-cid", GUINT_TO_POINTER (context_id));
}

static void
write_statusbar_message (EphyWindow *window, const char *message)
{
	GtkStatusbar *statusbar;
	guint context_id;

	statusbar = GTK_STATUSBAR (ephy_window_get_statusbar (window));
	g_return_if_fail (GTK_IS_STATUSBAR (statusbar));

	context_id = GPOINTER_TO_UINT (g_object_get_data (G_OBJECT (window), "foo-statusbar-cid"));
	g_return_if_fail (context_id != 0);

	/* Keep a maximum of one element on the stack */
	gtk_statusbar_pop (statusbar, context_id);

	/* Write our message */
	gtk_statusbar_push (statusbar, context_id, message);
}

static void
remove_statusbar_message (EphyWindow *window)
{
	GtkStatusbar *statusbar;
	guint context_id;

	statusbar = GTK_STATUSBAR (ephy_window_get_statusbar (window));
	g_return_if_fail (GTK_IS_STATUSBAR (statusbar));

	context_id = GPOINTER_TO_UINT (g_object_get_data (G_OBJECT (window), "foo-statusbar-cid"));
	g_return_if_fail (context_id != 0);

	gtk_statusbar_pop (statusbar, context_id);
}

static void
my_gconf_cb (GConfClient *client, guint cnxn_id, GConfEntry *entry, gpointer user_data)
{
	/* Do something */
}

guint cnxn_id;

cnxn_id = eel_gconf_notification_add ("/apps/epiphany/general/show_statusbar", (GConfClientNotifyFunc) my_gconf_cb, NULL);

extension->priv->gconf_cnxn_id = cnxn_id;
